import React from 'react';
import './App.scss';

import {
  Route,
  NavLink,
  Switch,
} from 'react-router-dom';

import List from './components/List';
import Detail from './components/Detail';

function App() {
  return (
    <div id="react-main-app">
      <div className="container">
        <h1><img src="data:image/svg+xml;base64," width="50" height="50" alt="React logo"/> React App</h1>
        <hr/>
        <p>
          This will be the layout.
        </p>
        <nav id="links">
          <NavLink to="/" activeClassName="active" exact="true">List</NavLink>
          <NavLink to="/detail" activeClassName="active" exact="true">Detail</NavLink>
        </nav>
        <Switch>
          <Route exact path="/" component={List} />
          <Route path="/detail" component={Detail} />
        </Switch>
      </div>
    </div>
  );
}

export default App;
